import React, { useState, useEffect, useRef } from 'react';
import { 
  Typography, 
  Grid, 
  Paper, 
  FormControl, 
  InputLabel, 
  Select, 
  MenuItem, 
  Button, 
  Divider, 
  CircularProgress, 
  Chip,
  makeStyles 
} from '@material-ui/core';
import CompareArrowsIcon from '@material-ui/icons/CompareArrows';
import DeleteIcon from '@material-ui/icons/Delete';
import Alert from '@material-ui/lab/Alert';

// This would be imported from a third-party library in a real implementation
const MoleculeViewer = ({ smiles, height = 250 }) => {
  const viewerRef = useRef(null);
  
  useEffect(() => {
    if (smiles && viewerRef.current) {
      // In a real implementation, this would initialize 3Dmol.js
      const placeholder = document.createElement('div');
      placeholder.style.width = '100%';
      placeholder.style.height = `${height}px`;
      placeholder.style.backgroundColor = '#f5f5f5';
      placeholder.style.display = 'flex';
      placeholder.style.alignItems = 'center';
      placeholder.style.justifyContent = 'center';
      
      const text = document.createElement('div');
      text.innerHTML = `<strong>SMILES:</strong> ${smiles}<br><br>3D Molecule Viewer would render here`;
      text.style.textAlign = 'center';
      text.style.padding = '20px';
      
      placeholder.appendChild(text);
      
      viewerRef.current.innerHTML = '';
      viewerRef.current.appendChild(placeholder);
    }
  }, [smiles, height]);
  
  return <div ref={viewerRef} style={{ width: '100%', height: `${height}px`, border: '1px solid #e0e0e0' }}></div>;
};

// This would be imported from a third-party library in a real implementation
const SimilarityChart = ({ molecules, similarity }) => {
  const chartRef = useRef(null);
  
  useEffect(() => {
    if (molecules && similarity && chartRef.current) {
      // In a real implementation, this would initialize a Plotly or D3 chart
      const placeholder = document.createElement('div');
      placeholder.style.width = '100%';
      placeholder.style.height = '300px';
      placeholder.style.backgroundColor = '#f5f5f5';
      placeholder.style.display = 'flex';
      placeholder.style.alignItems = 'center';
      placeholder.style.justifyContent = 'center';
      placeholder.style.flexDirection = 'column';
      
      const text = document.createElement('div');
      text.innerHTML = '<strong>Tanimoto Similarity</strong><br><br>';
      text.style.textAlign = 'center';
      text.style.padding = '20px';
      
      const similarityText = document.createElement('div');
      similarityText.style.fontSize = '2rem';
      similarityText.style.fontWeight = 'bold';
      similarityText.innerHTML = `${(similarity * 100).toFixed(1)}%`;
      
      const description = document.createElement('div');
      description.style.marginTop = '16px';
      description.innerHTML = `Similarity between ${molecules[0].name} and ${molecules[1].name}<br>Based on Morgan fingerprint Tanimoto coefficient`;
      
      placeholder.appendChild(text);
      placeholder.appendChild(similarityText);
      placeholder.appendChild(description);
      
      chartRef.current.innerHTML = '';
      chartRef.current.appendChild(placeholder);
    }
  }, [molecules, similarity]);
  
  return <div ref={chartRef} style={{ width: '100%', height: '300px', border: '1px solid #e0e0e0' }}></div>;
};

// This would be imported from a third-party library in a real implementation
const PropertyComparisonChart = ({ data }) => {
  const chartRef = useRef(null);
  
  useEffect(() => {
    if (data && chartRef.current) {
      // In a real implementation, this would initialize a Plotly or D3 chart
      const placeholder = document.createElement('div');
      placeholder.style.width = '100%';
      placeholder.style.height = '400px';
      placeholder.style.backgroundColor = '#f5f5f5';
      placeholder.style.display = 'flex';
      placeholder.style.alignItems = 'center';
      placeholder.style.justifyContent = 'center';
      
      const text = document.createElement('div');
      text.innerHTML = '<strong>Property Comparison Chart</strong><br><br>Bar chart comparing properties would render here';
      text.style.textAlign = 'center';
      text.style.padding = '20px';
      
      placeholder.appendChild(text);
      
      chartRef.current.innerHTML = '';
      chartRef.current.appendChild(placeholder);
    }
  }, [data]);
  
  return <div ref={chartRef} style={{ width: '100%', height: '400px', border: '1px solid #e0e0e0' }}></div>;
};

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
    padding: theme.spacing(3),
  },
  title: {
    marginBottom: theme.spacing(4),
    fontWeight: 500,
  },
  paper: {
    padding: theme.spacing(3),
    height: '100%',
  },
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
    width: '100%',
  },
  divider: {
    margin: theme.spacing(3, 0),
  },
  moleculeSelector: {
    display: 'flex',
    alignItems: 'center',
    marginBottom: theme.spacing(2),
  },
  compareButton: {
    marginTop: theme.spacing(2),
  },
  progress: {
    display: 'flex',
    justifyContent: 'center',
    margin: theme.spacing(4, 0),
  },
  propertyContainer: {
    marginTop: theme.spacing(2),
  },
  propertyRow: {
    padding: theme.spacing(1, 0),
    borderBottom: `1px solid ${theme.palette.divider}`,
    display: 'flex',
  },
  propertyLabel: {
    fontWeight: 500,
    flex: 1,
  },
  propertyValue: {
    flex: 1,
    textAlign: 'center',
  },
  better: {
    color: theme.palette.success.main,
    fontWeight: 500,
  },
  worse: {
    color: theme.palette.error.main,
    fontWeight: 500,
  },
  neutral: {
    color: theme.palette.text.primary,
  },
  sectionTitle: {
    marginTop: theme.spacing(3),
    marginBottom: theme.spacing(2),
  },
  chip: {
    margin: theme.spacing(0.5),
  },
  emptyState: {
    textAlign: 'center',
    padding: theme.spacing(6),
  },
  emptyStateIcon: {
    fontSize: 64,
    color: theme.palette.text.disabled,
    marginBottom: theme.spacing(2),
  },
  deleteButton: {
    marginLeft: theme.spacing(1),
  },
  alertMargin: {
    marginBottom: theme.spacing(2),
  },
}));

const ComparisonTool = () => {
  const classes = useStyles();
  const [loading, setLoading] = useState(false);
  const [selectedMolecules, setSelectedMolecules] = useState([null, null]);
  const [comparisonResults, setComparisonResults] = useState(null);
  const [error, setError] = useState(null);
  
  const molecules = [
    { id: 1, name: 'Methylphenidate', smiles: 'CN(C)C(C1=CC=CC=C1)C(C)OC(=O)C' },
    { id: 2, name: 'Amphetamine', smiles: 'CC(N)CC1=CC=CC=C1' },
    { id: 3, name: 'Atomoxetine', smiles: 'CC(C)NCC1=CC=CC(OC2=CC=CC=C2)=C1' },
    { id: 4, name: 'Novel Amphetamine Derivative', smiles: 'CC(CC1=CC=C(C=C1)O)NC' },
    { id: 5, name: 'Dextroamphetamine', smiles: 'CC(N)CC1=CC=CC=C1' },
    { id: 6, name: 'Lisdexamfetamine', smiles: 'CC(N)CC1=CC=CC=C1.NC(=O)C(N)CCCCN' }
  ];
  
  const handleMoleculeChange = (index, event) => {
    const value = event.target.value;
    const newSelectedMolecules = [...selectedMolecules];
    newSelectedMolecules[index] = value ? molecules.find(m => m.id === value) : null;
    setSelectedMolecules(newSelectedMolecules);
  };
  
  const handleClearMolecule = (index) => {
    const newSelectedMolecules = [...selectedMolecules];
    newSelectedMolecules[index] = null;
    setSelectedMolecules(newSelectedMolecules);
  };
  
  const handleCompare = async () => {
    if (!selectedMolecules[0] || !selectedMolecules[1]) {
      setError('Please select two molecules to compare');
      return;
    }
    
    setLoading(true);
    setError(null);
    
    try {
      // In a real implementation, this would make an API call to the backend
      await new Promise(resolve => setTimeout(resolve, 2000));
      
      // Sample comparison results
      setComparisonResults({
        molecules: selectedMolecules,
        similarity: 0.72,
        properties: {
          molecularWeight: {
            molecule1: 233.31,
            molecule2: 165.23,
            difference: -68.08,
            percentChange: -29.18
          },
          logP: {
            molecule1: 2.25,
            molecule2: 1.86,
            difference: -0.39,
            percentChange: -17.33
          },
          tpsa: {
            molecule1: 38.33,
            molecule2: 29.54,
            difference: -8.79,
            percentChange: -22.93
          },
          hDonors: {
            molecule1: 1,
            molecule2: 1,
            difference: 0
          },
          hAcceptors: {
            molecule1: 2,
            molecule2: 2,
            difference: 0
          },
          rotatableBonds: {
            molecule1: 4,
            molecule2: 3,
            difference: -1
          },
          lipinskiViolations: {
            molecule1: 0,
            molecule2: 0,
            difference: 0
          },
          qed: {
            molecule1: 0.69,
            molecule2: 0.78,
            difference: 0.09,
            percentChange: 13.04
          }
        },
        bindingAffinities: {
          dopamineTransporter: {
            molecule1: { score: 76, classification: 'Moderate' },
            molecule2: { score: 84, classification: 'Strong' },
            difference: 8
          },
          norepinephrineTransporter: {
            molecule1: { score: 68, classification: 'Moderate' },
            molecule2: { score: 72, classification: 'Moderate' },
            difference: 4
          },
          serotoninTransporter: {
            molecule1: { score: 42, classification: 'Moderate' },
            molecule2: { score: 35, classification: 'Weak' },
            difference: -7
          }
        },
        sideEffectProfile: {
          cardiovascular: {
            molecule1: 'Moderate',
            molecule2: 'Low'
          },
          insomnia: {
            molecule1: 'High',
            molecule2: 'Low'
          },
          appetiteSuppression: {
            molecule1: 'High',
            molecule2: 'Moderate'
          },
          anxiety: {
            molecule1: 'Moderate',
            molecule2: 'Low'
          }
        }
      });
    } catch (err) {
      console.error(err);
      setError('An error occurred during comparison. Please try again.');
    } finally {
      setLoading(false);
    }
  };
  
  const handleClearComparison = () => {
    setComparisonResults(null);
  };
  
  // Helper function to determine if a difference is better, worse, or neutral
  const getComparisonClass = (property, difference) => {
    // For these properties, higher is better
    if (property === 'qed') {
      return difference > 0 ? classes.better : difference < 0 ? classes.worse : classes.neutral;
    }
    
    // For these properties, lower is better
    if (property === 'logP' || property === 'tpsa' || property === 'lipinskiViolations' || 
        property === 'rotatableBonds' || property === 'molecularWeight') {
      return difference < 0 ? classes.better : difference > 0 ? classes.worse : classes.neutral;
    }
    
    // For other properties, no clear better/worse
    return classes.neutral;
  };
  
  const getBindingComparisonClass = (receptor, difference) => {
    // For dopamine and norepinephrine transporters, higher binding is better for ADHD treatment
    if (receptor === 'dopamineTransporter' || receptor === 'norepinephrineTransporter') {
      return difference > 0 ? classes.better : difference < 0 ? classes.worse : classes.neutral;
    }
    
    // For serotonin transporter, lower binding might be preferred to reduce side effects
    if (receptor === 'serotoninTransporter') {
      return difference < 0 ? classes.better : difference > 0 ? classes.worse : classes.neutral;
    }
    
    return classes.neutral;
  };
  
  const getSideEffectComparisonClass = (sideEffect, mol1, mol2) => {
    const severity = { 'Low': 1, 'Moderate': 2, 'High': 3 };
    
    // Lower side effect severity is always better
    if (severity[mol2] < severity[mol1]) {
      return classes.better;
    } else if (severity[mol2] > severity[mol1]) {
      return classes.worse;
    }
    
    return classes.neutral;
  };

  return (
    <div className={classes.root}>
      <Typography variant="h4" className={classes.title}>
        Molecule Comparison Tool
      </Typography>
      
      <Paper className={classes.paper}>
        <Typography variant="h6" gutterBottom>
          Select Molecules to Compare
        </Typography>
        
        <Grid container spacing={3}>
          <Grid item xs={12} md={5}>
            <div className={classes.moleculeSelector}>
              <FormControl variant="outlined" className={classes.formControl}>
                <InputLabel id="molecule1-label">Molecule 1</InputLabel>
                <Select
                  labelId="molecule1-label"
                  value={selectedMolecules[0]?.id || ''}
                  onChange={(e) => handleMoleculeChange(0, e)}
                  label="Molecule 1"
                >
                  <MenuItem value="">
                    <em>Select a molecule</em>
                  </MenuItem>
                  {molecules.map((molecule) => (
                    <MenuItem 
                      key={molecule.id} 
                      value={molecule.id}
                      disabled={selectedMolecules[1]?.id === molecule.id}
                    >
                      {molecule.name}
                    </MenuItem>
                  ))}
                </Select>
              </FormControl>
              
              {selectedMolecules[0] && (
                <Button 
                  className={classes.deleteButton}
                  onClick={() => handleClearMolecule(0)}
                  aria-label="clear molecule 1"
                >
                  <DeleteIcon fontSize="small" />
                </Button>
              )}
            </div>
            
            {selectedMolecules[0] && (
              <MoleculeViewer smiles={selectedMolecules[0].smiles} />
            )}
          </Grid>
          
          <Grid item xs={12} md={2} style={{ display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
            <CompareArrowsIcon style={{ fontSize: 48, color: '#757575' }} />
          </Grid>
          
          <Grid item xs={12} md={5}>
            <div className={classes.moleculeSelector}>
              <FormControl variant="outlined" className={classes.formControl}>
                <InputLabel id="molecule2-label">Molecule 2</InputLabel>
                <Select
                  labelId="molecule2-label"
                  value={selectedMolecules[1]?.id || ''}
                  onChange={(e) => handleMoleculeChange(1, e)}
                  label="Molecule 2"
                >
                  <MenuItem value="">
                    <em>Select a molecule</em>
                  </MenuItem>
                  {molecules.map((molecule) => (
                    <MenuItem 
                      key={molecule.id} 
                      value={molecule.id}
                      disabled={selectedMolecules[0]?.id === molecule.id}
                    >
                      {molecule.name}
                    </MenuItem>
                  ))}
                </Select>
              </FormControl>
              
              {selectedMolecules[1] && (
                <Button 
                  className={classes.deleteButton}
                  onClick={() => handleClearMolecule(1)}
                  aria-label="clear molecule 2"
                >
                  <DeleteIcon fontSize="small" />
                </Button>
              )}
            </div>
            
            {selectedMolecules[1] && (
              <MoleculeViewer smiles={selectedMolecules[1].smiles} />
            )}
          </Grid>
        </Grid>
        
        {error && (
          <Alert severity="error" className={classes.alertMargin} style={{ marginTop: 16 }}>
            {error}
          </Alert>
        )}
        
        <div style={{ textAlign: 'center', marginTop: 24 }}>
          {!comparisonResults ? (
            <Button
              variant="contained"
              color="primary"
              className={classes.compareButton}
              onClick={handleCompare}
              disabled={!selectedMolecules[0] || !selectedMolecules[1] || loading}
            >
              Compare Molecules
            </Button>
          ) : (
            <Button
              variant="outlined"
              color="primary"
              className={classes.compareButton}
              onClick={handleClearComparison}
            >
              Clear Comparison
            </Button>
          )}
        </div>
        
        {loading && (
          <div className={classes.progress}>
            <CircularProgress />
          </div>
        )}
        
        {comparisonResults && !loading && (
          <>
            <Divider className={classes.divider} />
            
            <Typography variant="h6" gutterBottom>
              Comparison Results: {comparisonResults.molecules[0].name} vs. {comparisonResults.molecules[1].name}
            </Typography>
            
            <Grid container spacing={3}>
              <Grid item xs={12} md={4}>
                <Typography variant="subtitle1" gutterBottom>
                  Structural Similarity
                </Typography>
                
                <SimilarityChart 
                  molecules={comparisonResults.molecules} 
                  similarity={comparisonResults.similarity} 
                />
                
                <div style={{ textAlign: 'center', marginTop: 8 }}>
                  <Chip 
                    label={comparisonResults.similarity >= 0.85 ? 'Very Similar' : 
                           comparisonResults.similarity >= 0.7 ? 'Similar' :
                           comparisonResults.similarity >= 0.5 ? 'Moderately Similar' :
                           'Distinct'}
                    className={classes.chip}
                    color="primary"
                    variant="outlined"
                  />
                </div>
              </Grid>
              
              <Grid item xs={12} md={8}>
                <Typography variant="subtitle1" gutterBottom>
                  Molecular Properties Comparison
                </Typography>
                
                <PropertyComparisonChart data={comparisonResults.properties} />
              </Grid>
            </Grid>
            
            <Typography variant="subtitle1" className={classes.sectionTitle}>
              Detailed Property Comparison
            </Typography>
            
            <div className={classes.propertyContainer}>
              <div className={classes.propertyRow} style={{ fontWeight: 500 }}>
                <div className={classes.propertyLabel}>Property</div>
                <div className={classes.propertyValue}>{comparisonResults.molecules[0].name}</div>
                <div className={classes.propertyValue}>{comparisonResults.molecules[1].name}</div>
                <div className={classes.propertyValue}>Difference</div>
              </div>
              
              {Object.entries(comparisonResults.properties).map(([property, values]) => (
                <div className={classes.propertyRow} key={property}>
                  <div className={classes.propertyLabel}>
                    {property === 'logP' ? 'LogP' :
                     property === 'tpsa' ? 'TPSA' :
                     property === 'hDonors' ? 'H-Bond Donors' :
                     property === 'hAcceptors' ? 'H-Bond Acceptors' :
                     property === 'qed' ? 'QED' :
                     property === 'lipinskiViolations' ? 'Lipinski Violations' :
                     property === 'rotatableBonds' ? 'Rotatable Bonds' :
                     property === 'molecularWeight' ? 'Molecular Weight' : property}
                  </div>
                  <div className={classes.propertyValue}>
                    {typeof values.molecule1 === 'number' ? values.molecule1.toFixed(2) : values.molecule1}
                    {property === 'molecularWeight' ? ' g/mol' : 
                     property === 'tpsa' ? ' Å²' : ''}
                  </div>
                  <div className={classes.propertyValue}>
                    {typeof values.molecule2 === 'number' ? values.molecule2.toFixed(2) : values.molecule2}
                    {property === 'molecularWeight' ? ' g/mol' : 
                     property === 'tpsa' ? ' Å²' : ''}
                  </div>
                  <div className={`${classes.propertyValue} ${getComparisonClass(property, values.difference)}`}>
                    {values.difference > 0 ? '+' : ''}{typeof values.difference === 'number' ? values.difference.toFixed(2) : values.difference}
                    {values.percentChange !== undefined && ` (${values.percentChange > 0 ? '+' : ''}${values.percentChange.toFixed(1)}%)`}
                  </div>
                </div>
              ))}
            </div>
            
            <Typography variant="subtitle1" className={classes.sectionTitle}>
              Receptor Binding Affinity Comparison
            </Typography>
            
            <div className={classes.propertyContainer}>
              <div className={classes.propertyRow} style={{ fontWeight: 500 }}>
                <div className={classes.propertyLabel}>Receptor</div>
                <div className={classes.propertyValue}>{comparisonResults.molecules[0].name}</div>
                <div className={classes.propertyValue}>{comparisonResults.molecules[1].name}</div>
                <div className={classes.propertyValue}>Difference</div>
              </div>
              
              {Object.entries(comparisonResults.bindingAffinities).map(([receptor, values]) => (
                <div className={classes.propertyRow} key={receptor}>
                  <div className={classes.propertyLabel}>
                    {receptor === 'dopamineTransporter' ? 'Dopamine Transporter' :
                     receptor === 'norepinephrineTransporter' ? 'Norepinephrine Transporter' :
                     receptor === 'serotoninTransporter' ? 'Serotonin Transporter' : receptor}
                  </div>
                  <div className={classes.propertyValue}>
                    {values.molecule1.score}/100 ({values.molecule1.classification})
                  </div>
                  <div className={classes.propertyValue}>
                    {values.molecule2.score}/100 ({values.molecule2.classification})
                  </div>
                  <div className={`${classes.propertyValue} ${getBindingComparisonClass(receptor, values.difference)}`}>
                    {values.difference > 0 ? '+' : ''}{values.difference}
                  </div>
                </div>
              ))}
            </div>
            
            <Typography variant="subtitle1" className={classes.sectionTitle}>
              Side Effect Profile Comparison
            </Typography>
            
            <div className={classes.propertyContainer}>
              <div className={classes.propertyRow} style={{ fontWeight: 500 }}>
                <div className={classes.propertyLabel}>Side Effect</div>
                <div className={classes.propertyValue}>{comparisonResults.molecules[0].name}</div>
                <div className={classes.propertyValue}>{comparisonResults.molecules[1].name}</div>
                <div className={classes.propertyValue}>Assessment</div>
              </div>
              
              {Object.entries(comparisonResults.sideEffectProfile).map(([sideEffect, values]) => (
                <div className={classes.propertyRow} key={sideEffect}>
                  <div className={classes.propertyLabel}>
                    {sideEffect === 'cardiovascular' ? 'Cardiovascular Effects' :
                     sideEffect === 'insomnia' ? 'Insomnia' :
                     sideEffect === 'appetiteSuppression' ? 'Appetite Suppression' :
                     sideEffect === 'anxiety' ? 'Anxiety' : sideEffect}
                  </div>
                  <div className={classes.propertyValue}>
                    {values.molecule1}
                  </div>
                  <div className={classes.propertyValue}>
                    {values.molecule2}
                  </div>
                  <div className={`${classes.propertyValue} ${getSideEffectComparisonClass(sideEffect, values.molecule1, values.molecule2)}`}>
                    {values.molecule1 === values.molecule2 ? 'No change' :
                     values.molecule2 === 'Low' ? 'Improved' :
                     values.molecule1 === 'High' && values.molecule2 === 'Moderate' ? 'Improved' :
                     'Worsened'}
                  </div>
                </div>
              ))}
            </div>
            
            <Divider className={classes.divider} />
            
            <Grid container spacing={2}>
              <Grid item xs={12} sm={6}>
                <Button variant="outlined" color="primary" fullWidth>
                  Export Comparison
                </Button>
              </Grid>
              <Grid item xs={12} sm={6}>
                <Button variant="contained" color="primary" fullWidth>
                  Add to Report
                </Button>
              </Grid>
            </Grid>
          </>
        )}
        
        {!comparisonResults && !loading && (
          <div className={classes.emptyState}>
            <CompareArrowsIcon className={classes.emptyStateIcon} />
            <Typography variant="h6" color="textSecondary">
              Select molecules to compare
            </Typography>
            <Typography variant="body2" color="textSecondary">
              Choose two molecules and click "Compare Molecules" to see a detailed comparison
            </Typography>
          </div>
        )}
      </Paper>
    </div>
  );
};

export default ComparisonTool;
