import React, { useState } from 'react';
import { 
  Typography, 
  Grid, 
  Paper, 
  TextField, 
  Button, 
  InputAdornment,
  List,
  ListItem,
  ListItemText,
  Divider,
  CircularProgress,
  Chip,
  makeStyles 
} from '@material-ui/core';
import SearchIcon from '@material-ui/icons/Search';
import MenuBookIcon from '@material-ui/icons/MenuBook';
import FormatQuoteIcon from '@material-ui/icons/FormatQuote';

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
    padding: theme.spacing(3),
  },
  title: {
    marginBottom: theme.spacing(4),
    fontWeight: 500,
  },
  paper: {
    padding: theme.spacing(3),
    height: '100%',
  },
  searchBar: {
    marginBottom: theme.spacing(3),
  },
  searchButton: {
    height: 56,
    marginLeft: theme.spacing(1),
  },
  articleItem: {
    padding: theme.spacing(2),
    marginBottom: theme.spacing(2),
    borderLeft: `3px solid ${theme.palette.primary.main}`,
    transition: 'all 0.2s',
    '&:hover': {
      backgroundColor: '#f5f5f5',
      transform: 'translateX(5px)',
    },
  },
  articleTitle: {
    fontWeight: 500,
  },
  articleInfo: {
    display: 'flex',
    justifyContent: 'space-between',
    marginTop: theme.spacing(1),
    marginBottom: theme.spacing(1),
    color: theme.palette.text.secondary,
    fontSize: '0.875rem',
  },
  abstractText: {
    marginTop: theme.spacing(1),
    marginBottom: theme.spacing(1),
    fontSize: '0.9rem',
    lineHeight: 1.6,
  },
  chip: {
    margin: theme.spacing(0.5),
  },
  progress: {
    display: 'flex',
    justifyContent: 'center',
    margin: theme.spacing(4, 0),
  },
  iconSpacing: {
    marginRight: theme.spacing(1),
  },
  articleDetail: {
    padding: theme.spacing(3),
  },
  articleDetailTitle: {
    marginBottom: theme.spacing(2),
  },
  divider: {
    margin: theme.spacing(2, 0),
  },
  citation: {
    fontStyle: 'italic',
    marginBottom: theme.spacing(2),
  },
  abstractTitle: {
    marginTop: theme.spacing(2),
    marginBottom: theme.spacing(1),
    fontWeight: 500,
  },
  emptyState: {
    textAlign: 'center',
    padding: theme.spacing(6),
  },
  emptyStateIcon: {
    fontSize: 64,
    color: theme.palette.text.disabled,
    marginBottom: theme.spacing(2),
  },
}));

const LiteratureExplorer = () => {
  const classes = useStyles();
  const [searchQuery, setSearchQuery] = useState('');
  const [loading, setLoading] = useState(false);
  const [articles, setArticles] = useState([]);
  const [selectedArticle, setSelectedArticle] = useState(null);
  
  const handleSearchChange = (e) => {
    setSearchQuery(e.target.value);
  };
  
  const handleSearch = async () => {
    if (!searchQuery.trim()) return;
    
    setLoading(true);
    setSelectedArticle(null);
    
    try {
      // In a real implementation, this would make an API call to the backend
      await new Promise(resolve => setTimeout(resolve, 1500));
      
      // Sample data
      setArticles([
        {
          id: 'PMC1234567',
          title: 'Novel Amphetamine Derivatives with Reduced Cardiovascular Effects in ADHD Treatment',
          authors: 'Smith J, Johnson R, Williams T',
          journal: 'Journal of Medicinal Chemistry',
          year: 2021,
          abstract: 'Amphetamine-based medications have been the mainstay of ADHD treatment for decades, but cardiovascular side effects remain a significant concern. This study describes the synthesis and evaluation of a series of novel amphetamine derivatives with structural modifications aimed at reducing interaction with adrenergic receptors while maintaining dopaminergic activity. Several compounds demonstrated promising activity in preclinical models with significantly reduced effects on blood pressure and heart rate compared to traditional amphetamines.',
          keywords: ['ADHD', 'Amphetamine derivatives', 'Cardiovascular side effects', 'Dopamine', 'Drug design'],
          citation: 'Smith J, Johnson R, Williams T. Novel Amphetamine Derivatives with Reduced Cardiovascular Effects in ADHD Treatment. J Med Chem. 2021;64(15):10823-10836. doi:10.1021/acs.jmedchem.1c01234'
        },
        {
          id: 'PMC2345678',
          title: 'Structure-Activity Relationships of Non-Stimulant ADHD Treatments',
          authors: 'Chen Y, Garcia P, Martinez L',
          journal: 'European Journal of Medicinal Chemistry',
          year: 2022,
          abstract: 'This comprehensive review examines the structure-activity relationships of non-stimulant treatments for attention-deficit/hyperactivity disorder (ADHD). We analyze chemical structures, receptor binding profiles, and clinical outcomes of approved medications like atomoxetine, guanfacine, and viloxazine, as well as compounds in development. The review highlights promising molecular scaffolds and pharmacophore features that may guide future drug development efforts for ADHD treatments with improved efficacy and reduced side effect profiles.',
          keywords: ['ADHD', 'Non-stimulant', 'Atomoxetine', 'Structure-activity relationship', 'Drug development'],
          citation: 'Chen Y, Garcia P, Martinez L. Structure-Activity Relationships of Non-Stimulant ADHD Treatments. Eur J Med Chem. 2022;227:113923. doi:10.1016/j.ejmech.2021.113923'
        },
        {
          id: 'PMC3456789',
          title: 'Pharmacokinetic and Pharmacodynamic Evaluation of Novel Extended-Release Methylphenidate Formulations',
          authors: 'Brown A, Miller D, Wilson S',
          journal: 'Clinical Pharmacokinetics',
          year: 2020,
          abstract: 'Extended-release formulations of methylphenidate have been developed to overcome the limitations of immediate-release formulations in ADHD treatment. This study evaluates the pharmacokinetic and pharmacodynamic properties of three novel extended-release methylphenidate formulations. The formulations demonstrated distinct plasma concentration-time profiles, resulting in different patterns of efficacy throughout the day. The study provides insights into the relationship between formulation technology, pharmacokinetic profile, and clinical response in ADHD patients.',
          keywords: ['Methylphenidate', 'ADHD', 'Extended-release', 'Pharmacokinetics', 'Formulation technology'],
          citation: 'Brown A, Miller D, Wilson S. Pharmacokinetic and Pharmacodynamic Evaluation of Novel Extended-Release Methylphenidate Formulations. Clin Pharmacokinet. 2020;59(8):1045-1060. doi:10.1007/s40262-020-00892-0'
        }
      ]);
    } catch (error) {
      console.error('Error searching literature:', error);
    } finally {
      setLoading(false);
    }
  };
  
  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSearch();
    }
  };
  
  const handleArticleSelect = (article) => {
    setSelectedArticle(article);
  };
  
  const handleBackToResults = () => {
    setSelectedArticle(null);
  };

  return (
    <div className={classes.root}>
      <Typography variant="h4" className={classes.title}>
        Literature Explorer
      </Typography>
      
      <Grid container spacing={3}>
        <Grid item xs={12}>
          <Paper className={classes.paper}>
            <Grid container className={classes.searchBar}>
              <Grid item xs>
                <TextField
                  fullWidth
                  variant="outlined"
                  placeholder="Search for research papers, patents, and clinical trials..."
                  value={searchQuery}
                  onChange={handleSearchChange}
                  onKeyPress={handleKeyPress}
                  InputProps={{
                    startAdornment: (
                      <InputAdornment position="start">
                        <SearchIcon />
                      </InputAdornment>
                    ),
                  }}
                />
              </Grid>
              <Grid item>
                <Button
                  variant="contained"
                  color="primary"
                  className={classes.searchButton}
                  onClick={handleSearch}
                  disabled={loading}
                >
                  Search
                </Button>
              </Grid>
            </Grid>
            
            {loading ? (
              <div className={classes.progress}>
                <CircularProgress />
              </div>
            ) : selectedArticle ? (
              <div className={classes.articleDetail}>
                <Button color="primary" onClick={handleBackToResults}>
                  ← Back to results
                </Button>
                
                <Typography variant="h5" className={classes.articleDetailTitle}>
                  {selectedArticle.title}
                </Typography>
                
                <Typography variant="body2" className={classes.citation}>
                  {selectedArticle.citation}
                </Typography>
                
                <Divider className={classes.divider} />
                
                <Typography variant="subtitle1" className={classes.abstractTitle}>
                  Abstract
                </Typography>
                
                <Typography variant="body1" paragraph>
                  <FormatQuoteIcon fontSize="small" className={classes.iconSpacing} />
                  {selectedArticle.abstract}
                </Typography>
                
                <Typography variant="subtitle1" className={classes.abstractTitle}>
                  Keywords
                </Typography>
                
                {selectedArticle.keywords.map((keyword, index) => (
                  <Chip
                    key={index}
                    label={keyword}
                    className={classes.chip}
                    variant="outlined"
                    color="primary"
                    size="small"
                  />
                ))}
                
                <Divider className={classes.divider} />
                
                <Grid container spacing={2}>
                  <Grid item xs={12} sm={6}>
                    <Button variant="outlined" color="primary" fullWidth>
                      Download PDF
                    </Button>
                  </Grid>
                  <Grid item xs={12} sm={6}>
                    <Button variant="outlined" color="primary" fullWidth>
                      Add to Reference Library
                    </Button>
                  </Grid>
                </Grid>
              </div>
            ) : articles.length > 0 ? (
              <List>
                {articles.map((article) => (
                  <Paper 
                    key={article.id} 
                    className={classes.articleItem}
                    onClick={() => handleArticleSelect(article)}
                  >
                    <Typography variant="h6" className={classes.articleTitle}>
                      {article.title}
                    </Typography>
                    
                    <div className={classes.articleInfo}>
                      <Typography variant="body2">
                        {article.authors}
                      </Typography>
                      <Typography variant="body2">
                        {article.journal} ({article.year})
                      </Typography>
                    </div>
                    
                    <Typography 
                      variant="body2" 
                      className={classes.abstractText}
                      noWrap
                    >
                      {article.abstract}
                    </Typography>
                    
                    <div>
                      {article.keywords.slice(0, 3).map((keyword, index) => (
                        <Chip
                          key={index}
                          label={keyword}
                          size="small"
                          className={classes.chip}
                          variant="outlined"
                        />
                      ))}
                      {article.keywords.length > 3 && (
                        <Chip
                          label={`+${article.keywords.length - 3} more`}
                          size="small"
                          className={classes.chip}
                          variant="outlined"
                        />
                      )}
                    </div>
                  </Paper>
                ))}
              </List>
            ) : (
              <div className={classes.emptyState}>
                <MenuBookIcon className={classes.emptyStateIcon} />
                <Typography variant="h6" color="textSecondary">
                  Search for literature
                </Typography>
                <Typography variant="body2" color="textSecondary">
                  Enter keywords to search for research papers related to ADHD treatments
                </Typography>
              </div>
            )}
          </Paper>
        </Grid>
      </Grid>
    </div>
  );
};

export default LiteratureExplorer;
