import React, { useState } from 'react';
import { 
  ThemeProvider, 
  createTheme, 
  makeStyles,
  CssBaseline,
  AppBar,
  Toolbar,
  Typography,
  Tabs,
  Tab,
  Box,
  Container,
  IconButton,
  Button,
  Drawer,
  List,
  ListItem,
  ListItemIcon,
  ListItemText,
  Divider
} from '@material-ui/core';
import MenuIcon from '@material-ui/icons/Menu';
import HomeIcon from '@material-ui/icons/Home';
import BuildIcon from '@material-ui/icons/Build'; // Using Build icon instead of Science
import DescriptionIcon from '@material-ui/icons/Description';
import CompareArrowsIcon from '@material-ui/icons/CompareArrows';
import TimelineIcon from '@material-ui/icons/Timeline';
import AccountCircleIcon from '@material-ui/icons/AccountCircle';

// Import all the components
import MoleculeDesigner from './components/MoleculeDesigner';
import SimulationPanel from './components/SimulationPanel';
import LiteratureExplorer from './components/LiteratureExplorer';
import ComparisonTool from './components/ComparisonTool';
import RegulatoryAnalysis from './components/RegulatoryAnalysis';

// Create TabPanel component
function TabPanel(props) {
  const { children, value, index, ...other } = props;

  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`simple-tabpanel-${index}`}
      aria-labelledby={`simple-tab-${index}`}
      {...other}
    >
      {value === index && (
        <Box p={3}>
          {children}
        </Box>
      )}
    </div>
  );
}

// Define Claude-inspired theme based on provided specs
const claudeTheme = createTheme({
  palette: {
    primary: {
      main: '#DA7756', // Terra-cotta
      light: '#E49273', // Lighter terracotta
      dark: '#BD5D3A', // Richer terracotta for buttons/accents
      contrastText: '#FFFFFF',
    },
    secondary: {
      // Using a complementary subtle color, maybe a muted blue/gray
      main: '#A0ACBD', 
      light: '#D1D9E3',
      dark: '#707D8B',
      contrastText: '#3D3929',
    },
    error: {
      main: '#D32F2F', // Standard Material Red
    },
    warning: {
      main: '#FFA000', // Standard Material Amber
    },
    info: {
      main: '#1976D2', // Standard Material Blue
    },
    success: {
      main: '#388E3C', // Standard Material Green
    },
    background: {
      default: '#EEECE2', // Off-white
      paper: '#FFFFFF',
    },
    text: {
      primary: '#3D3929', // Dark charcoal
      secondary: '#6D6A5B', // Lighter charcoal/brown
      disabled: '#BDBDBD',
    },
    divider: 'rgba(61, 57, 41, 0.12)', // Based on text primary
  },
  typography: {
    fontFamily: [
      'Geist', // Attempt to use Geist first if loaded
      'Styrene', // Then Styrene
      'Tiempos', // Then Tiempos
      'ui-serif', // Fallback stacks
      'Georgia',
      'Cambria',
      '"Times New Roman"' ,
      'Times',
      'serif',
      'Inter', // Include Inter as general sans-serif fallback
      'Roboto',
      '"Helvetica Neue"',
      'Arial',
      'sans-serif'
    ].join(','),
    // Keep heading weights, colors will be inherited from text.primary
    h4: { fontWeight: 600 },
    h5: { fontWeight: 600 },
    h6: { fontWeight: 600 },
    // Body colors are inherited from text palette
  },
  shape: {
    borderRadius: 6, // Slightly less rounded than before
  },
  overrides: {
    MuiAppBar: {
      colorPrimary: {
        backgroundColor: '#3D3929', // Dark charcoal AppBar
        color: '#EEECE2', // Off-white text on AppBar
      },
    },
    MuiButton: {
      root: {
        textTransform: 'none',
        borderRadius: 6,
      },
      containedPrimary: {
        backgroundColor: '#DA7756', // Primary terra-cotta
        color: '#FFFFFF',
        '&:hover': {
          backgroundColor: '#BD5D3A', // Darker accent for hover
        },
      },
      containedSecondary: {
        backgroundColor: '#A0ACBD',
        color: '#3D3929',
        '&:hover': {
           backgroundColor: '#8A98A8',
        },
      },
      outlinedPrimary: {
          borderColor: '#DA7756',
          color: '#DA7756',
          '&:hover': {
            backgroundColor: 'rgba(218, 119, 86, 0.04)' // Faint terracotta background on hover
          }
      }
    },
    MuiTabs: {
      indicator: {
        backgroundColor: '#DA7756', // Terracotta indicator
        height: 3,
        borderTopLeftRadius: 3,
        borderTopRightRadius: 3,
      },
    },
    MuiTab: {
      root: {
        textTransform: 'none',
        fontWeight: 500,
        '&.Mui-selected': {
            color: '#BD5D3A' // Darker terracotta for selected tab text
        },
        color: '#6D6A5B' // Secondary text color for unselected tabs
      },
      // Keep wrapper override from previous theme
      wrapper: {
        flexDirection: 'row',
        alignItems: 'center',
        '& > *:first-child': {
          marginBottom: '0 !important',
          marginRight: 8,
        },
      },
    },
    MuiPaper: {
        // Use softer shadows consistent with minimalist UI
      elevation1: {
        boxShadow: '0px 1px 3px rgba(61, 57, 41, 0.1)', 
      },
      elevation3: {
        boxShadow: '0px 2px 6px rgba(61, 57, 41, 0.1)',
      },
    },
    MuiListItem: {
      button: {
        '&:hover': {
          backgroundColor: 'rgba(218, 119, 86, 0.04)', // Faint primary hover
        },
      },
      root: {
        '&.Mui-selected': {
          backgroundColor: 'rgba(218, 119, 86, 0.08)', // Slightly more primary for selected
          '&:hover': {
            backgroundColor: 'rgba(218, 119, 86, 0.12)',
          }
        },
      },
    },
    MuiListItemIcon: {
      root: {
        // Icons in lists should probably match secondary text color for subtlety
        color: '#6D6A5B', 
        minWidth: 40,
      },
    },
    MuiChip: {
        root: {
            backgroundColor: 'rgba(61, 57, 41, 0.08)' // Subtle background for chips
        },
        colorPrimary: {
            backgroundColor: '#DA7756', // Primary chip background
            color: '#FFFFFF'
        },
        colorSecondary: {
            backgroundColor: '#A0ACBD', // Secondary chip background
            color: '#3D3929'
        },
        outlinedPrimary: {
            borderColor: '#DA7756',
            color: '#DA7756'
        },
        outlinedSecondary: {
            borderColor: '#A0ACBD',
            color: '#A0ACBD'
        }
    },
    // Add specific overrides for chat bubbles if needed, 
    // although AIChatInterface now uses theme colors directly.
  },
});

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
    display: 'flex',
    flexDirection: 'column',
    minHeight: '100vh',
  },
  appBar: {
    zIndex: theme.zIndex.drawer + 1,
  },
  title: {
    flexGrow: 1,
  },
  drawer: {
    width: 240,
    flexShrink: 0,
  },
  drawerPaper: {
    width: 240,
  },
  drawerContainer: {
    overflow: 'auto',
  },
  content: {
    flexGrow: 1,
    padding: 0,
  },
  toolbar: theme.mixins.toolbar,
  tabsContainer: {
    backgroundColor: '#fff',
    boxShadow: '0 1px 3px rgba(0,0,0,0.12)',
  },
  tab: {
    minHeight: 72,
  },
  tabPanel: {
    paddingTop: theme.spacing(2),
  },
  userChip: {
    backgroundColor: theme.palette.primary.dark,
    color: '#fff',
    marginRight: theme.spacing(2),
  },
  userName: {
    marginLeft: theme.spacing(1),
  },
}));

function App() {
  const classes = useStyles();
  const [tabValue, setTabValue] = useState(0);
  const [drawerOpen, setDrawerOpen] = useState(false);

  const handleTabChange = (event, newValue) => {
    setTabValue(newValue);
  };

  const toggleDrawer = () => {
    setDrawerOpen(!drawerOpen);
  };

  const menuItems = [
    { text: 'Dashboard', icon: <HomeIcon /> },
    { text: 'Molecule Designer', icon: <BuildIcon /> },
    { text: 'Simulations', icon: <TimelineIcon /> },
    { text: 'Literature Explorer', icon: <DescriptionIcon /> },
    { text: 'Comparison Tool', icon: <CompareArrowsIcon /> },
    { text: 'Regulatory Analysis', icon: <TimelineIcon /> },
  ];

  return (
    <ThemeProvider theme={claudeTheme}>
      <CssBaseline />
      <div className={classes.root}>
        <AppBar position="fixed" className={classes.appBar}>
          <Toolbar>
            <IconButton
              edge="start"
              color="inherit"
              aria-label="menu"
              onClick={toggleDrawer}
            >
              <MenuIcon />
            </IconButton>
            <Typography variant="h6" className={classes.title}>
              Breaking Good | Drug Discovery Platform
            </Typography>
            <Button color="inherit" startIcon={<AccountCircleIcon />}>
              Login
            </Button>
          </Toolbar>
        </AppBar>

        <Drawer
          className={classes.drawer}
          variant="temporary"
          open={drawerOpen}
          onClose={toggleDrawer}
          classes={{
            paper: classes.drawerPaper,
          }}
        >
          <div className={classes.toolbar} />
          <div className={classes.drawerContainer}>
            <List>
              {menuItems.map((item, index) => (
                <ListItem 
                  button 
                  key={item.text}
                  selected={index === tabValue}
                  onClick={() => {
                    setTabValue(index);
                    setDrawerOpen(false);
                  }}
                >
                  <ListItemIcon>{item.icon}</ListItemIcon>
                  <ListItemText primary={item.text} />
                </ListItem>
              ))}
            </List>
            <Divider />
            <List>
              <ListItem button>
                <ListItemIcon><DescriptionIcon /></ListItemIcon>
                <ListItemText primary="Documentation" />
              </ListItem>
            </List>
          </div>
        </Drawer>

        <main className={classes.content}>
          <div className={classes.toolbar} />
          
          <Box className={classes.tabsContainer}>
            <Container>
              <Tabs
                value={tabValue}
                onChange={handleTabChange}
                variant="scrollable"
                scrollButtons="auto"
                indicatorColor="primary"
                textColor="primary"
                aria-label="app navigation tabs"
              >
                <Tab className={classes.tab} icon={<HomeIcon />} label="Dashboard" />
                <Tab className={classes.tab} icon={<BuildIcon />} label="Molecule Designer" />
                <Tab className={classes.tab} icon={<TimelineIcon />} label="Simulations" />
                <Tab className={classes.tab} icon={<DescriptionIcon />} label="Literature Explorer" />
                <Tab className={classes.tab} icon={<CompareArrowsIcon />} label="Comparison Tool" />
                <Tab className={classes.tab} icon={<TimelineIcon />} label="Regulatory Analysis" />
              </Tabs>
            </Container>
          </Box>

          <Container className={classes.tabPanel}>
            <TabPanel value={tabValue} index={0}>
              <Typography variant="h4" gutterBottom>
                Dashboard
              </Typography>
              <Typography paragraph>
                Welcome to Breaking Good - an innovative platform for neuropharmacology drug discovery focused on designing safer alternatives to Adderall for ADHD treatment.
              </Typography>
              <Typography paragraph>
                Navigate through the tabs to access different tools and functionalities of the platform. You can design molecules, run simulations, explore scientific literature, compare different molecules, and analyze regulatory pathways.
              </Typography>
            </TabPanel>
            
            <TabPanel value={tabValue} index={1}>
              <MoleculeDesigner />
            </TabPanel>
            
            <TabPanel value={tabValue} index={2}>
              <SimulationPanel />
            </TabPanel>
            
            <TabPanel value={tabValue} index={3}>
              <LiteratureExplorer />
            </TabPanel>
            
            <TabPanel value={tabValue} index={4}>
              <ComparisonTool />
            </TabPanel>
            
            <TabPanel value={tabValue} index={5}>
              <RegulatoryAnalysis />
            </TabPanel>
          </Container>
        </main>
      </div>
    </ThemeProvider>
  );
}

export default App;
