import React, { useState } from 'react';
import { 
  ThemeProvider, 
  createTheme, 
  makeStyles,
  CssBaseline,
  AppBar,
  Toolbar,
  Typography,
  Tabs,
  Tab,
  Box,
  Container,
  IconButton,
  Button,
  Drawer,
  List,
  ListItem,
  ListItemIcon,
  ListItemText,
  Divider
} from '@material-ui/core';
import MenuIcon from '@material-ui/icons/Menu';
import HomeIcon from '@material-ui/icons/Home';
import BuildIcon from '@material-ui/icons/Build'; // Using Build icon instead of Science
import DescriptionIcon from '@material-ui/icons/Description';
import CompareArrowsIcon from '@material-ui/icons/CompareArrows';
import TimelineIcon from '@material-ui/icons/Timeline';
import AccountCircleIcon from '@material-ui/icons/AccountCircle';

// Import all the components
import MoleculeDesigner from './components/MoleculeDesigner';
import SimulationPanel from './components/SimulationPanel';
import LiteratureExplorer from './components/LiteratureExplorer';
import ComparisonTool from './components/ComparisonTool';
import RegulatoryAnalysis from './components/RegulatoryAnalysis';

// Create TabPanel component
function TabPanel(props) {
  const { children, value, index, ...other } = props;

  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`simple-tabpanel-${index}`}
      aria-labelledby={`simple-tab-${index}`}
      {...other}
    >
      {value === index && (
        <Box p={3}>
          {children}
        </Box>
      )}
    </div>
  );
}

// Define Claude-inspired theme
const claudeTheme = createTheme({
  palette: {
    primary: {
      main: '#6A5ACD', // SlateBlue - A typical Claude-like purple
      light: '#9370DB', // MediumPurple
      dark: '#483D8B', // DarkSlateBlue
      contrastText: '#ffffff',
    },
    secondary: {
      main: '#DDA0DD', // Plum - Lighter purple
      light: '#E6E6FA', // Lavender
      dark: '#BA55D3', // MediumOrchid
      contrastText: '#333333',
    },
    error: {
      main: '#f44336',
    },
    warning: {
      main: '#ff9800',
    },
    info: {
      main: '#2196f3',
    },
    success: {
      main: '#4caf50',
    },
    background: {
      default: '#f8f8ff', // GhostWhite - Light background
      paper: '#ffffff', // White paper background
    },
    text: {
      primary: '#333333', // Dark gray for primary text
      secondary: '#757575', // Medium gray for secondary text
      disabled: '#bdbdbd',
    },
    divider: 'rgba(0, 0, 0, 0.12)',
  },
  typography: {
    fontFamily: [
      'Inter', // A clean sans-serif often associated with modern UI
      'Roboto',
      '"Helvetica Neue"' ,
      'Arial',
      'sans-serif'
    ].join(','),
    h4: {
      fontWeight: 600, // Slightly bolder headings
      color: '#333333',
    },
    h5: {
      fontWeight: 600,
      color: '#333333',
    },
    h6: {
      fontWeight: 600,
      color: '#333333',
    },
    subtitle1: {
      color: '#555555',
    },
    body1: {
      color: '#444444',
    },
    body2: {
      color: '#666666',
    },
  },
  shape: {
    borderRadius: 8, // Slightly more rounded corners
  },
  overrides: {
    MuiAppBar: {
      colorPrimary: {
        backgroundColor: '#483D8B', // Darker purple for AppBar
      },
    },
    MuiButton: {
      root: {
        textTransform: 'none', // More modern button text
        borderRadius: 6,
      },
      containedPrimary: {
        '&:hover': {
          backgroundColor: '#5A4CAD',
        },
      },
    },
    MuiTabs: {
      indicator: {
        height: 3,
        borderTopLeftRadius: 3,
        borderTopRightRadius: 3,
      },
    },
    MuiTab: {
      root: {
        textTransform: 'none',
        fontWeight: 500,
      },
      wrapper: {
        flexDirection: 'row',
        alignItems: 'center',
        '& > *:first-child': {
          marginBottom: '0 !important',
          marginRight: 8,
        },
      },
    },
    MuiPaper: {
      elevation1: {
        boxShadow: '0px 2px 4px -1px rgba(0,0,0,0.1), 0px 4px 5px 0px rgba(0,0,0,0.07), 0px 1px 10px 0px rgba(0,0,0,0.06)', // Softer shadow
      },
      elevation3: {
        boxShadow: '0px 3px 5px -1px rgba(0,0,0,0.1), 0px 5px 8px 0px rgba(0,0,0,0.07), 0px 1px 14px 0px rgba(0,0,0,0.06)',
      },
    },
    MuiListItem: {
      button: {
        '&:hover': {
          backgroundColor: 'rgba(106, 90, 205, 0.08)', // Use primary light color for hover
        },
      },
      root: {
        '&.Mui-selected': {
          backgroundColor: 'rgba(106, 90, 205, 0.12)', // More subtle selection
          '&:hover': {
            backgroundColor: 'rgba(106, 90, 205, 0.15)',
          }
        },
      },
    },
    MuiListItemIcon: {
      root: {
        color: 'inherit', // Ensure icon color matches text
        minWidth: 40,
      },
    },
  },
});

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
    display: 'flex',
    flexDirection: 'column',
    minHeight: '100vh',
  },
  appBar: {
    zIndex: theme.zIndex.drawer + 1,
  },
  title: {
    flexGrow: 1,
  },
  drawer: {
    width: 240,
    flexShrink: 0,
  },
  drawerPaper: {
    width: 240,
  },
  drawerContainer: {
    overflow: 'auto',
  },
  content: {
    flexGrow: 1,
    padding: 0,
  },
  toolbar: theme.mixins.toolbar,
  tabsContainer: {
    backgroundColor: '#fff',
    boxShadow: '0 1px 3px rgba(0,0,0,0.12)',
  },
  tab: {
    minHeight: 72,
  },
  tabPanel: {
    paddingTop: theme.spacing(2),
  },
  userChip: {
    backgroundColor: theme.palette.primary.dark,
    color: '#fff',
    marginRight: theme.spacing(2),
  },
  userName: {
    marginLeft: theme.spacing(1),
  },
}));

function App() {
  const classes = useStyles();
  const [tabValue, setTabValue] = useState(0);
  const [drawerOpen, setDrawerOpen] = useState(false);

  const handleTabChange = (event, newValue) => {
    setTabValue(newValue);
  };

  const toggleDrawer = () => {
    setDrawerOpen(!drawerOpen);
  };

  const menuItems = [
    { text: 'Dashboard', icon: <HomeIcon /> },
    { text: 'Molecule Designer', icon: <BuildIcon /> },
    { text: 'Simulations', icon: <TimelineIcon /> },
    { text: 'Literature Explorer', icon: <DescriptionIcon /> },
    { text: 'Comparison Tool', icon: <CompareArrowsIcon /> },
    { text: 'Regulatory Analysis', icon: <TimelineIcon /> },
  ];

  return (
    <ThemeProvider theme={claudeTheme}>
      <CssBaseline />
      <div className={classes.root}>
        <AppBar position="fixed" className={classes.appBar}>
          <Toolbar>
            <IconButton
              edge="start"
              color="inherit"
              aria-label="menu"
              onClick={toggleDrawer}
            >
              <MenuIcon />
            </IconButton>
            <Typography variant="h6" className={classes.title}>
              Breaking Good | Drug Discovery Platform
            </Typography>
            <Button color="inherit" startIcon={<AccountCircleIcon />}>
              Login
            </Button>
          </Toolbar>
        </AppBar>

        <Drawer
          className={classes.drawer}
          variant="temporary"
          open={drawerOpen}
          onClose={toggleDrawer}
          classes={{
            paper: classes.drawerPaper,
          }}
        >
          <div className={classes.toolbar} />
          <div className={classes.drawerContainer}>
            <List>
              {menuItems.map((item, index) => (
                <ListItem 
                  button 
                  key={item.text}
                  selected={index === tabValue}
                  onClick={() => {
                    setTabValue(index);
                    setDrawerOpen(false);
                  }}
                >
                  <ListItemIcon>{item.icon}</ListItemIcon>
                  <ListItemText primary={item.text} />
                </ListItem>
              ))}
            </List>
            <Divider />
            <List>
              <ListItem button>
                <ListItemIcon><DescriptionIcon /></ListItemIcon>
                <ListItemText primary="Documentation" />
              </ListItem>
            </List>
          </div>
        </Drawer>

        <main className={classes.content}>
          <div className={classes.toolbar} />
          
          <Box className={classes.tabsContainer}>
            <Container>
              <Tabs
                value={tabValue}
                onChange={handleTabChange}
                variant="scrollable"
                scrollButtons="auto"
                indicatorColor="primary"
                textColor="primary"
                aria-label="app navigation tabs"
              >
                <Tab className={classes.tab} icon={<HomeIcon />} label="Dashboard" />
                <Tab className={classes.tab} icon={<BuildIcon />} label="Molecule Designer" />
                <Tab className={classes.tab} icon={<TimelineIcon />} label="Simulations" />
                <Tab className={classes.tab} icon={<DescriptionIcon />} label="Literature Explorer" />
                <Tab className={classes.tab} icon={<CompareArrowsIcon />} label="Comparison Tool" />
                <Tab className={classes.tab} icon={<TimelineIcon />} label="Regulatory Analysis" />
              </Tabs>
            </Container>
          </Box>

          <Container className={classes.tabPanel}>
            <TabPanel value={tabValue} index={0}>
              <Typography variant="h4" gutterBottom>
                Dashboard
              </Typography>
              <Typography paragraph>
                Welcome to Breaking Good - an innovative platform for neuropharmacology drug discovery focused on designing safer alternatives to Adderall for ADHD treatment.
              </Typography>
              <Typography paragraph>
                Navigate through the tabs to access different tools and functionalities of the platform. You can design molecules, run simulations, explore scientific literature, compare different molecules, and analyze regulatory pathways.
              </Typography>
            </TabPanel>
            
            <TabPanel value={tabValue} index={1}>
              <MoleculeDesigner />
            </TabPanel>
            
            <TabPanel value={tabValue} index={2}>
              <SimulationPanel />
            </TabPanel>
            
            <TabPanel value={tabValue} index={3}>
              <LiteratureExplorer />
            </TabPanel>
            
            <TabPanel value={tabValue} index={4}>
              <ComparisonTool />
            </TabPanel>
            
            <TabPanel value={tabValue} index={5}>
              <RegulatoryAnalysis />
            </TabPanel>
          </Container>
        </main>
      </div>
    </ThemeProvider>
  );
}

export default App;
