import React, { useState } from 'react';
import { 
  ThemeProvider, 
  createTheme, 
  makeStyles,
  CssBaseline,
  AppBar,
  Toolbar,
  Typography,
  Tabs,
  Tab,
  Box,
  Container,
  IconButton,
  Button,
  Drawer,
  List,
  ListItem,
  ListItemIcon,
  ListItemText,
  Divider
} from '@material-ui/core';
import MenuIcon from '@material-ui/icons/Menu';
import HomeIcon from '@material-ui/icons/Home';
import BuildIcon from '@material-ui/icons/Build'; // Using Build icon instead of Science
import DescriptionIcon from '@material-ui/icons/Description';
import CompareArrowsIcon from '@material-ui/icons/CompareArrows';
import TimelineIcon from '@material-ui/icons/Timeline';
import AccountCircleIcon from '@material-ui/icons/AccountCircle';

// Import all the components
import LandingPage from './components/LandingPage';
import MoleculeDesigner from './components/MoleculeDesigner';
import SimulationPanel from './components/SimulationPanel';
import LiteratureExplorer from './components/LiteratureExplorer';
import ComparisonTool from './components/ComparisonTool';
import RegulatoryAnalysis from './components/RegulatoryAnalysis';

// Create TabPanel component
function TabPanel(props) {
  const { children, value, index, ...other } = props;

  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`simple-tabpanel-${index}`}
      aria-labelledby={`simple-tab-${index}`}
      {...other}
    >
      {value === index && (
        <Box p={3}>
          {children}
        </Box>
      )}
    </div>
  );
}

// Define Landing Page Theme
const landingTheme = createTheme({
  palette: {
    type: 'light',
    primary: {
      main: '#E27B58', // Terracotta Orange
      light: '#F29E7A', // Lighter accent for hover
      dark: '#C4644A', // Darker accent for hover
      contrastText: '#FFFFFF',
    },
    secondary: {
      main: '#4F4F4F', // Warm Gray (used for secondary text)
      contrastText: '#FFFFFF',
    },
    background: {
      default: '#FBF9F7', // Off-white
      paper: '#FFFFFF',
    },
    text: {
      primary: '#111111', // Charcoal Black
      secondary: '#4F4F4F', // Warm Gray
      disabled: '#BDBDBD',
    },
    divider: '#EAE8E4', // Very light gray
  },
  typography: {
    // Set up the stacks. Actual rendering depends on font loading.
    fontFamily: [
      'Tiempos', // Default body
      'ui-serif', 'Georgia', 'Cambria', '"Times New Roman"', 'Times', 'serif',
      'Styrene', // Available for headings
      'Inter', 'Helvetica Neue', 'Arial', 'sans-serif' // General sans-serif
    ].join(','),
    // Define specific variants based on spec
